import React, { useEffect, useState } from 'react';
import { View, Text, FlatList, StyleSheet, TouchableOpacity, Modal, Button, TextInput, Alert } from 'react-native';
import axios from 'axios';
import Icon from 'react-native-vector-icons/FontAwesome'; 

function Objetos() {
  const [categorias, setCategorias] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedItem, setSelectedItem] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [newObject, setNewObject] = useState({ Nombre: '', Categoria: '' });
  const [isAddingObject, setIsAddingObject] = useState(false);

  useEffect(() => {
    const fetchCategorias = async () => {
      try {
        const response = await axios.get('http://localhost:3000/objeto');
        setCategorias(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error al obtener la lista de objetos:', error);
        Alert.alert('Error', 'No se pudo cargar la lista de objetos.');
      }
    };

    fetchCategorias();
  }, []);

  const handleBack = () => {
    navigation.navigate('CategoriasScreen');
  };

  const requestLoan = async (objectId) => {
    try {
      // Obtener el FK_Usuario de la solicitud (ajusta esto según tu lógica)
      const userId = 1; // Ejemplo: debes obtener el ID del usuario de alguna manera

      if (!userId) {
        Alert.alert('Error', 'Usuario no válido para realizar la solicitud de préstamo.');
        return;
      }

      // Realizar la solicitud de préstamo con el FK_Usuario proporcionado
      const response = await axios.post('http://localhost:3000/prestamo', {
        FK_Objeto: objectId,
        FK_Usuario: userId, // Asegúrate de que esto esté configurado correctamente
        // Agrega otros campos necesarios para tu solicitud
      });

      console.log('Respuesta del servidor:', response.data);

      Alert.alert('Solicitud de Préstamo', 'La solicitud se ha enviado con éxito.');

      // Actualizar la lista de objetos después de la solicitud
      await fetchCategorias();
    } catch (error) {
      console.error('Error al solicitar préstamo:', error);
      Alert.alert('Error', 'No se pudo enviar la solicitud de préstamo.');
    }
  };

  const handleItemPress = (item) => {
    setSelectedItem(item);
    setShowModal(true);
  };

  const handleConfirm = () => {
    if (selectedItem) {
      requestLoan(selectedItem.Id);
    }
  };

  return (
    <View style={styles.container}>
      {/* ... (código anterior) */}

      <FlatList
        data={categorias}
        keyExtractor={(item) => item.Id.toString()}
        renderItem={({ item }) => (
          <View style={styles.objectContainer}>
            <TouchableOpacity style={styles.itemContainer} onPress={() => handleItemPress(item)}>
              <Text style={styles.category}>{item.Nombre}</Text>
              <Button title="Solicitar Préstamo" onPress={() => requestLoan(item.Id)} />
            </TouchableOpacity>
          </View>
        )}
        ItemSeparatorComponent={() => <View style={styles.separator} />}
      />

      {/* ... (código anterior) */}
    </View>
  );
}

const styles = StyleSheet.create({
  // ... (código anterior)
});

export default Objetos;



