import React, { useState } from 'react';
import { View, Text, TextInput, Button } from 'react-native';
import { useNavigation } from '@react-navigation/native';

function Login() {
  const [dni, setDni] = useState('');
  const [clave, setClave] = useState('');
  const navigation = useNavigation();

  const handleLogin = () => {
    // Aquí puedes realizar la lógica de validación y autenticación sin utilizar el servicio UserService

    // Ejemplo simple: Verificar si el DNI y la clave no están vacíos
    if (dni.trim() === '' || clave.trim() === '') {
      console.error('Credenciales incorrectas');
      // Mostrar mensaje de error al usuario
    } else {
      // Autenticación exitosa, navegar a la pantalla 'Home'
      navigation.navigate('Home');
    }
  };

  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', paddingHorizontal: 20 }}>
      <Text style={{ fontSize: 24, marginBottom: 20 }}>Iniciar Sesión</Text>
      <View style={{ marginBottom: 15, width: '100%' }}>
        <Text>DNI:</Text>
        <TextInput
          style={{ borderWidth: 1, padding: 10, borderRadius: 5 }}
          placeholder="Ingrese su DNI"
          value={dni}
          onChangeText={(text) => setDni(text)}
        />
      </View>
      <View style={{ marginBottom: 15, width: '100%' }}>
        <Text>Clave:</Text>
        <TextInput
          style={{ borderWidth: 1, padding: 10, borderRadius: 5 }}
          placeholder="Ingrese su clave"
          value={clave}
          onChangeText={(text) => setClave(text)}
          secureTextEntry
        />
      </View>
      <Button title="Iniciar Sesión" onPress={handleLogin} />
    </View>
  );
}

export default Login;
