import React, { useState } from 'react';
import { View, Text, TextInput, Button } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { UserService } from '../../Backend/src/services/userService'; 

function Login() {
  const [dni, setDni] = useState('');
  const [clave, setClave] = useState('');
  const navigation = useNavigation();

  const handleLogin = async () => {
    try {
      const response = await UserService.GetUserLogin({ usuario: dni, clave }); // Usa UserService en lugar de userService

      if (response) {
        navigation.navigate('Home');
      } else {
        console.error('Credenciales incorrectas');
        // Mostrar mensaje de error al usuario
      }
    } catch (error) {
      console.error('Error al iniciar sesi贸n:', error.message);
      // Mostrar mensaje de error al usuario
    }
  };

  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', paddingHorizontal: 20 }}>
      <Text style={{ fontSize: 24, marginBottom: 20 }}>Iniciar Sesi贸n</Text>
      <View style={{ marginBottom: 15, width: '100%' }}>
        <Text>DNI:</Text>
        <TextInput
          style={{ borderWidth: 1, padding: 10, borderRadius: 5 }}
          placeholder="Ingrese su DNI"
          value={dni}
          onChangeText={(text) => setDni(text)}
        />
      </View>
      <View style={{ marginBottom: 15, width: '100%' }}>
        <Text>Clave:</Text>
        <TextInput
          style={{ borderWidth: 1, padding: 10, borderRadius: 5 }}
          placeholder="Ingrese su clave"
          value={clave}
          onChangeText={(text) => setClave(text)}
          secureTextEntry
        />
      </View>
      <Button title="Iniciar Sesi贸n" onPress={handleLogin} />
    </View>
  );
}

export default Login;
