import React, { useEffect, useState } from 'react';
import { View, Text, FlatList, StyleSheet, TouchableOpacity } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import axios from 'axios';
import Icon from 'react-native-vector-icons/FontAwesome';

function ReportesAdmin() {
  const navigation = useNavigation();
  const [reportes, setReportes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchReportes = async () => {
      try {
        const response = await axios.get('http://localhost:3000/reporte');
        setReportes(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error(error);
      }
    };

    fetchReportes();
  }, []);

  const handleBack = () => {
    navigation.navigate('Home');
  };

  const renderReporteItem = ({ item }) => (
    <View style={styles.categoryBox}>
      <Text style={styles.category}>{item.ObjetoFallo}</Text>
      <Text>{item.Descripcion}</Text>
      {/* Puedes agregar más detalles o información del reporte según tus necesidades */}
    </View>
  );

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity onPress={handleBack}>
          <Icon name="arrow-left" size={24} color="white" style={styles.backButtonIcon} />
        </TouchableOpacity>
        <Text style={styles.headerText}> Listado de reportes:</Text>
      </View>

      {isLoading ? (
        <Text>Cargando reportes...</Text>
      ) : (
        <FlatList
  data={reportes}
  keyExtractor={(item) => (item.id ? item.id.toString() : Math.random().toString())}
  renderItem={renderReporteItem}
  style={{ width: '100%' }}
/>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    backgroundColor: '#007BFF',
    padding: 10,
    width: '100%',
    alignItems: 'center',
    marginBottom: 10,
    flexDirection: 'row',
  },
  headerText: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'white',
    flex: 1,
  },
  backButtonIcon: {
    fontSize: 24,
    marginLeft: 10,
  },
  categoryBox: {
    backgroundColor: 'lightgray',
    padding: 10,
    borderRadius: 8,
    marginBottom: 10,
  },
  category: {
    fontSize: 18,
    marginBottom: 5,
  },
});

export default ReportesAdmin;
